import React, { useState } from 'react'

const Textform = (props) => {
    let [text,setText]=useState('kaveri mehndi');

    const texteriaval=(event)=>{
        setText(event.target.value)
    }
    const updateText=()=>{
       const manuplate= text.toUpperCase()
        setText(manuplate)
    }

return (
<div>{text}
<h3>{props.middle}</h3>
<div className="mb-3">
<textarea className="form-control" value={text} id="Empaty Box" rows="7" cols="70" onChange={texteriaval}></textarea>
<button className="btn btn-primary my-4" onClick={updateText}>Convert to primary</button>

</div>

        </div>
    )
}

export default Textform

Textform.defaultProps={
middle:"Enter Your Details"
}
